import { StyleSheet, Text, View, Pressable, ScrollView, SafeAreaView, TextInput, Platform } from 'react-native';
import { Feather, AntDesign } from "@expo/vector-icons";

const HomeScreens = () => {
    return (
        <SafeAreaView
            style={{
                paddingTop: Platform.OS === "android" ? 40 : 0,
                flex: 1,
                backgroundColor: "white",
            }}
        >
            <ScrollView>
                <View
                    style={{
                        backgroundColor: "#00CED1",
                        padding: 10,
                        flexDirection: "row",
                        alignItems: "center",
                    }}
                >
                    <Pressable
                        style={{
                            flexDirection: "row",
                            alignItems: "center",
                            marginHorizontal: 7,
                            gap: 10,
                            backgroundColor: "white",
                            borderRadius: 5,
                            height: 38,
                            flex: 1,
                        }}
                    >
                        <AntDesign
                            style={{ paddingLeft: 10 }}
                            name="search1"
                            size={24}
                            color="black"
                        />
                        <TextInput placeholder="Search Amazon.in" />
                    </Pressable>
                    <Feather name="mic" size={24} color="black" />
                </View>


            </ScrollView>
        </SafeAreaView>
    );
}

export default HomeScreens;

const styles = StyleSheet.create({});
