import React from 'react';
import { StyleSheet, Text, View, Pressable, ScrollView, SafeAreaView, TextInput, Platform, Image } from 'react-native';
import { Feather, AntDesign, Ionicons, MaterialIcons } from "@expo/vector-icons";
import { SliderBox } from "react-native-image-slider-box";

const HomeScreens = () => {
    const list = [
        {
            id: "0",
            image: "https://m.media-amazon.com/images/I/41EcYoIZhIL._AC_SY400_.jpg",
            name: "Home",
        },
        {
            id: "1",
            image: "https://m.media-amazon.com/images/G/31/img20/Events/Jup21dealsgrid/blockbuster.jpg",
            name: "Deals",
        },
        {
            id: "2",
            image: "https://images-eu.ssl-images-amazon.com/images/I/31dXEvtxidL._AC_SX368_.jpg",
            name: "Electronics",
        },
    ];

    const images = [
        "https://img.etimg.com/thumb/msid-93051525,width-1070,height-580,imgsize-2243475,overlay-economictimes/photo.jpg",
        "https://images-eu.ssl-images-amazon.com/images/G/31/img22/Wireless/devjyoti/PD23/Launches/Updated_ingress1242x550_3.gif",
        "https://images-eu.ssl-images-amazon.com/images/G/31/img23/Books/BB/JULY/1242x550_Header-BB-Jul23.jpg",
    ];

    return (
        <SafeAreaView
            style={{
                paddingTop: Platform.OS === "android" ? 40 : 0,
                flex: 1,
                backgroundColor: "white",
            }}
        >
            <ScrollView>
                <View
                    style={{
                        backgroundColor: "#00CED1",
                        padding: 10,
                        flexDirection: "row",
                        alignItems: "center",
                    }}
                >
                    <Pressable
                        style={{
                            flexDirection: "row",
                            alignItems: "center",
                            marginHorizontal: 7,
                            gap: 10,
                            backgroundColor: "white",
                            borderRadius: 5,
                            height: 38,
                            flex: 1,
                        }}
                    >
                        <AntDesign
                            style={{ paddingLeft: 10 }}
                            name="search1"
                            size={24}
                            color="black"
                        />
                        <TextInput placeholder="Search Amazon.in" />
                    </Pressable>
                    <Feather name="mic" size={24} color="black" />
                </View>

                <View
                    style={{
                        flexDirection: "row",
                        alignItems: "center",
                        gap: 5,
                        padding: 10,
                        backgroundColor: "#AFEEEE",
                    }}
                >
                    <Ionicons name="location-outline" size={24} color="black" />
                    <Pressable>
                        <Text style={{ fontSize: 13, fontWeight: "500" }}>
                            Add an Address Fullstack
                        </Text>
                    </Pressable>
                    <MaterialIcons name="keyboard-arrow-down" size={24} color="black" />
                </View>

                <ScrollView horizontal showsHorizontalScrollIndicator={false}>
                    {list.map((item, index) => (
                        <Pressable
                            key={index}
                            style={{
                                margin: 10,
                                justifyContent: "center",
                                alignItems: "center",
                            }}
                        >
                            <Image
                                style={{ width: 50, height: 50, resizeMode: "contain" }}
                                source={{ uri: item.image }}
                            />
                            <Text
                                style={{
                                    textAlign: "center",
                                    fontSize: 12,
                                    fontWeight: "500",
                                    marginTop: 5,
                                }}
                            >
                                {item?.name}
                            </Text>
                        </Pressable>
                    ))}
                </ScrollView>

                <SliderBox
                    images={images}
                    autoPlay
                    circleLoop
                    dotColor={"#13274F"}
                    inactiveDotColor="#90A4AE"
                    ImageComponentStyle={{ width: "100%" }}
                />
            </ScrollView>
        </SafeAreaView>
    );
}

export default HomeScreens;

const styles = StyleSheet.create({});
